mapper.select:
SELECT filenamemac
  FROM attach
 WHERE numMac=#{numMac}

mapper.insert:
INSERT INTO ATTACH (NUMMAC, PCODEMAC, IDMAC, NICKNAMEMAC, WDATEMAC, FILENAMEMAC, FILEPATHMAC) SELECT ATTACH_NUM.NEXTVAL, t.* FROM (
SELECT #{item.pcodeMac}, #{item.idMac}, #{item.nickNameMac}, SYSDATE, #{item.fileNameMac}, #{item.filepathMac} FROM DUAL
) t
